import logo from './logo.svg';
import './App.css';
import React, { useState } from "react" 
import axios from 'axios';
import $ from 'jquery';

function App() {
  const [form, setForm] = useState({
    name: "",
    businessName: "",
    servive_photo: false,
    servive_integ: false,
    servive_host: false,
    product_type:"",
    product_quantity:"",
    location:"",
    email:""
  })



  function checkPhotoService() {

    if(document.getElementById("cb1").checked = true){
      form.servive_photo = true;
    }
    else{
      form.servive_photo = false;
    }

}

  function checkIntegService() {

    if(document.getElementById("cb2").checked = true){
      form.servive_integ = true;
    }
    else{
      form.servive_integ = false;
    }

}

function checkHostService() {

  if(document.getElementById("cb3").checked = true){
    form.servive_host = true;
  }
  else{
    form.servive_host = false;
  }

}

function uncheck() {
    document.getElementById("myCheck").checked = false;
}

  const productTypesArray = [
    {
      id:0,
      name:"Shoes",
    },
    {
      id:1,
      name:"Clothing",
    },
    {
      id:3,
      name:"Transport Veihcles",
    },
    {
      id:4,
      name:"Real Estate",
    },
    {
      id:5,
      name:"Consumer Goods",
    },
    {
      id:6,
      name:"Interior Design",
    }
]

const productQuantityArray = [
  {
    id:0,
    name:"up to 25",
  },
  {
    id:1,
    name:"up to 50",
  },
  {
    id:3,
    name:"Up to 100",
  },
  {
    id:4,
    name:"Up to 250",
  },
  {
    id:5,
    name:"Up to 500",
  },
  {
    id:6,
    name:"More than a 1000",
  }
]

const locationArray = [
  {
    id:0,
    name:"Lisbon",
  },
  {
    id:1,
    name:"Oporto",
  },
  {
    id:3,
    name:"Madrid",
  },
  {
    id:4,
    name:"Barcelona",
  },
  {
    id:5,
    name:"London",
  },
  {
    id:6,
    name:"Frankfurt",
  }
]

  const [count, setCount] = useState(1)

  const updateForm = (e) => {



    setForm({
      ...form,
      [e.target.name]: e.target.value,
    })
  }

/**
 * alert(
            `Submitted 
            Name: ${form.name} 
            BusinessName:${form.businessName} 
            PhotoService: ${form.servive_photo}
            IntegrationService: ${form.servive_integ}
            HostingService: ${form.servive_host}
            ProductType: ${form.product_type}
            ProductQuantity: ${form.product_quantity}
            Location:${form.location}
            Email: ${form.email} 
            `
          )
 */



 function authenticate(){

console.log('forming')

  axios.post('http://localhost:8080/authenticate', {
    username: 'foo',
    password: 'foo'
  })
  .then((response) => {
    console.log(response);
  }, (error) => {
    console.log(error);
  });

 }
 /*
 axios.interceptors.headers.use(
  config => {
    config.headers.authorization = "Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJmb28iLCJleHAiOjE2Mjc5NDAxMTQsImlhdCI6MTYyNzkwNDExNH0.R8q-mvBfNe1X-sRH3tIPQXPKytJJz_cqdEdrxddp32M"
  return config;
  },

  error => {
    return Promise.reject;
  }
)
  */
function sendform(){

  const dataSend = {
    "user":{
        "personName":"Miguel",
            "businessName":"Apple",
            "email":"m@a.com"
    },
    "serviceType":[{
        "name":"nameServiceType",
        "description":"descriptionservicetype"
    }],

    "productType":{
        "name":"nameProductType",
        "size":"ProductSize",
        "complexity":"a"
    },

    "productQuantity":{
        "quantity":"23"
    },
    "location":{
        "name":"lisbon"
    }

}

  const url = 'http://localhost:8080/createForm'
  const data = dataSend;
  const headers = {
    "Authorization": "eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJmb28iLCJleHAiOjE2Mjc5NDAxMTQsImlhdCI6MTYyNzkwNDExNH0.R8q-mvBfNe1X-sRH3tIPQXPKytJJz_cqdEdrxddp32M"

  }



  axios.post(url, data, headers).then((response) => {
    console.log(response);
    console.log(response.headers.Authorization);
  }, (error) => {
    console.log(error);
    console.log(error.Authorization);
  });
/*
  axios.post('http://localhost:8080/createForm', {
    data
  },  {headers:
    {"Authorization": "eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJmb28iLCJleHAiOjE2Mjc5NDAxMTQsImlhdCI6MTYyNzkwNDExNH0.R8q-mvBfNe1X-sRH3tIPQXPKytJJz_cqdEdrxddp32M"}}

  )
  .then((response) => {
    console.log(response);
  }, (error) => {
    console.log(error);
  });

*/
}


  return (
    <div className="App">
      <h1>Step {count} of 3</h1>
      <button onClick={sendform}>Test http</button>
      <form
        className="col-4 form"
        onSubmit={() => sendform


        }
      >
        {count === 1 ? (
          <div className="form-group">
            <label>Your name</label>
            <input
              className="form-control"
              name="name"
              onChange={updateForm}
              value={form.name}
            />
            <label>Name of your business</label>
            <input
              className="form-control"
              name="businessName"
              onChange={updateForm}
              value={form.businessName}
            />
          </div>
        ) : null}
        {count === 2 ? (
          <div className="form-group">
            <label>Select the services you need to present your products in a 360 View</label>
            <label>To convert your catalog into 360 you have several  processes including <br></br> 360 photgraphy and software integrations to display and <br></br> allow your clients to interact with your product</label>
            <ul>
              <li ><input type="checkbox"  id="cb1" name="servive_photo"
                          onChange={checkPhotoService}
                          value={form.servive_photo}/>
                <label for="cb1">360 Photography</label>
              </li>
              <li ><input type="checkbox"id="cb2" name="servive_integ"
                          onChange={checkIntegService}
                          value={form.servive_integ}/>
                <label for="cb2">WebSite Integration or Digital Catalog</label>
              </li>
              <li ><input type="checkbox"  id="cb3" name="servive_host" type="checkbox"
                          onChange={checkHostService}
                            />
                <label for="cb3">Hosting</label>
              </li>
            </ul>
          </div>
        ) : null}
        {count === 3 ? (

          <div className="form-group">
            <label>Select your product type</label>
            <select name="product_type" id="product_type" onChange={updateForm} value={form.product_type}>
            {
              productTypesArray.map(listItem => ( 
                <option key={listItem.id} value={listItem.name}>{listItem.name}</option>))
              }
            </select>

            <label>Select the number of products in your catalog</label>
            <select name="product_quantity" id="product_quantity" onChange={updateForm} value={form.product_quantity}>
            {
              productQuantityArray.map(listItem => ( 
                <option value={listItem.name}>{listItem.name}</option>))
              }
            </select>

            <label>Select the nearest location to your business</label>
            <select name="location" id="location" onChange={updateForm} value={form.location}>
            {
              locationArray.map(listItem => ( 
                <option value={listItem.name}>{listItem.name}</option>))
              }
            </select>
          </div>

        ) : null}

        {count === 4 ? (
          <div className="form-group">
          <label>Enter your email, so we can see send you the calculations</label>
          <input
            type="email"
            className="form-control"
            name="email"
            onChange={updateForm}
            value={form.email}
          />
          </div>
        ):null}

        {count === 4 ? (


          <button className="btn btn-primary" type="submit">
            Submit
          </button>
        ) : null}


      </form>
      <button
        className="btn btn-dark"
        type="submit"
        onClick={() => setCount(count - 1)}
        disabled={count < 2}
      >
        Back
      </button>
      <button
        className="btn btn-light"
        type="submit"
        onClick={() => setCount(count + 1)}
        disabled={count > 3}
      >
        Next
      </button>
    </div>
  );
}

export default App;
