import logo from './logo.svg';
import './App.css';
import React, { useState } from "react"

function App() {
  const [form, setForm] = useState({
    fullName: "",
    name: "",
    password: "",
  })
  const [count, setCount] = useState(1)

  const updateForm = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    })
  }
  return (
    <div className="App">
      <h1>Step {count} of 3</h1>
      <form
        className="col-4 form"
        onSubmit={() =>
          alert(
            `Submitted Email: ${form.fullName} Name: ${form.name} Password: ${form.password}`
          )
        }
      >
        {count === 1 ? (
          <div className="form-group">
            <label>Your name</label>
            <input
              type="email"
              className="form-control"
              name="email"
              onChange={updateForm}
              value={form.fullName}
            />
            <label>Name of your business</label>
            <input
              type="email"
              className="form-control"
              name="email"
              onChange={updateForm}
              value={form.email}
            />
          </div>
        ) : null}
        {count === 2 ? (
          <div className="form-group">
            <label>Select the services you need to present your products in a 360 View</label>
            <input type="checkbox" id="myCheckbox1" 
            name="name"
            onChange={updateForm}
              value={form.name}/>
            <label for="myCheckbox1"><img src="http://someurl" /></label>
            <ul>
              <li ><input type="checkbox" hidden="true" id="cb1" />
                <label for="cb1"><img src="https://picsum.photos/seed/1/100" /></label>
              </li>
              <li><input type="checkbox" id="cb2" />
                <label for="cb2"><img src="https://picsum.photos/seed/2/100" /></label>
                </li>
              <li><input type="checkbox" id="cb3" />
                  <label for="cb3"><img src="https://picsum.photos/seed/3/100" /></label>
              </li>
              <li><input type="checkbox" id="cb4" />
                  <label for="cb4"><img src="https://picsum.photos/seed/4/100" /></label>
              </li>
            </ul>


          </div>
        ) : null}
        {count === 3 ? (
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              onChange={updateForm}
              value={form.password}
            />
          </div>
        ) : null}
        {count === 3 ? (
          <button className="btn btn-primary" type="submit">
            Submit
          </button>
        ) : null}
      </form>
      <button
        className="btn btn-dark"
        type="submit"
        onClick={() => setCount(count - 1)}
        disabled={count < 2}
      >
        Back
      </button>
      <button
        className="btn btn-light"
        type="submit"
        onClick={() => setCount(count + 1)}
        disabled={count > 2}
      >
        Next
      </button>
    </div>
  );
}

export default App;
