import logo from './logo.svg';
import './App.css';
import React, { useState } from "react"

function App() {
  const [form, setForm] = useState({
    name: "",
    businessName: "",
    servive_photo: false,
    servive_integ: false,
    servive_host: false,
    product_type:"",
    product_quantity:"",
    location:"",
    email:""
  })

  const productTypesArray = [
    {
      id:0,
      name:"Shoes",
    },
    {
      id:1,
      name:"Clothing",
    },
    {
      id:3,
      name:"Transport Veihcles",
    },
    {
      id:4,
      name:"Real Estate",
    },
    {
      id:5,
      name:"Consumer Goods",
    },
    {
      id:6,
      name:"Interior Design",
    }
]

const productQuantityArray = [
  {
    id:0,
    name:"up to 25",
  },
  {
    id:1,
    name:"up to 50",
  },
  {
    id:3,
    name:"Up to 100",
  },
  {
    id:4,
    name:"Up to 250",
  },
  {
    id:5,
    name:"Up to 500",
  },
  {
    id:6,
    name:"More than a 1000",
  }
]

const locationArray = [
  {
    id:0,
    name:"Lisbon",
  },
  {
    id:1,
    name:"Oporto",
  },
  {
    id:3,
    name:"Madrid",
  },
  {
    id:4,
    name:"Barcelona",
  },
  {
    id:5,
    name:"London",
  },
  {
    id:6,
    name:"Frankfurt",
  }
]

  const [count, setCount] = useState(1)

  const updateForm = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    })
  }
  return (
    <div className="App">
      <h1>Step {count} of 3</h1>
      <form
        className="col-4 form"
        onSubmit={() =>
          alert(
            `Submitted Email: ${form.fullName} Name: ${form.name} Password: ${form.password}`
          )
        }
      >
        {count === 1 ? (
          <div className="form-group">
            <label>Your name</label>
            <input
              type="email"
              className="form-control"
              name="email"
              onChange={updateForm}
              value={form.fullName}
            />
            <label>Name of your business</label>
            <input
              type="email"
              className="form-control"
              name="email"
              onChange={updateForm}
              value={form.email}
            />
          </div>
        ) : null}
        {count === 2 ? (
          <div className="form-group">
            <label>Select the services you need to present your products in a 360 View</label>
            <input type="checkbox" id="myCheckbox1" 
            name="name"
            onChange={updateForm}
              value={form.name}/>
            <label>To convert your catalog into 360 you have several processes including 360 photgraphy and software integrations to display and allow your clients to interact with your product</label>
            <ul>
              <li ><input type="checkbox"  id="cb1" name="name"
                          onChange={updateForm}
                          value={form.servive_photo}/>
                <label for="cb1">360 Photography</label>
              </li>
              <li ><input type="checkbox"id="cb2" 
                          onChange={updateForm}
                          value={form.servive_integ}/>
                <label for="cb2">WebSite Integration or Digital Catalog</label>
              </li>
              <li ><input type="checkbox"  id="cb3" 
                          onChange={updateForm}
                          value={form.servive_host}/>
                <label for="cb3">Hosting</label>
              </li>
            </ul>
          </div>
        ) : null}
        {count === 3 ? (

          <div className="form-group">
            <label>Select your product type</label>
            <select name="product_type" id="product_type" onChange={updateForm} value={form.product_type}>
            {
              productTypesArray.map(listItem => ( 
                <option value="product_type">{listItem.name}</option>))
              }
            </select>

            <label>Select the number of products in your catalog</label>
            <select name="product_quantity" id="product_quantity" onChange={updateForm} value={form.product_type}>
            {
              productQuantityArray.map(listItem => ( 
                <option value="product_quantity">{listItem.name}</option>))
              }
            </select>

            <label>Select the nearest location to your business</label>
            <select name="location" id="location" onChange={updateForm} value={form.product_type}>
            {
              locationArray.map(listItem => ( 
                <option value="location">{listItem.name}</option>))
              }
            </select>
          </div>

        ) : null}

        {count === 4 ? (
          <div className="form-group">
          <label>Enter your email, so we can see send you the calculations</label>
          <input
            type="email"
            className="form-control"
            name="email"
            onChange={updateForm}
            value={form.email}
          />
          </div>
        ):null}

        {count === 4 ? (


          <button className="btn btn-primary" type="submit">
            Submit
          </button>
        ) : null}


      </form>
      <button
        className="btn btn-dark"
        type="submit"
        onClick={() => setCount(count - 1)}
        disabled={count < 3}
      >
        Back
      </button>
      <button
        className="btn btn-light"
        type="submit"
        onClick={() => setCount(count + 1)}
        disabled={count > 3}
      >
        Next
      </button>
    </div>
  );
}

export default App;
