        onClick={() => setCount(count - 1)}
        disabled={count < 2}
        style={{display:isFinalPage ? "none" : "inline"}}
      >
        Back
      </button>
      <button
        className="btn btn-light"
        type="submit"
        onClick={() => setCount(count + 1)}
        disabled={count > 3}
        style={{display:isFinalPage ? "none" : "inline"}}

      >
        Next
      </button>
    </div>
  );
}

export default App;
